import React, { useState } from 'react';
import HeroSlider, { Slide } from 'hero-slider';
import { Grid, Typography, Paper } from '@mui/material';
import styled from '@emotion/styled';
import Background1 from '../../img/wallpaper_lap.jpeg';
import Background2 from '../../img/download.jpeg';
import Background3 from '../../img/laptop-transparent-png-pictures-icons-and-png-40.png';
import Background4 from '../../img/murjp1nk4lp1idlt.jpg';

const Item = styled(Paper)(({ theme }) => ({

}));

const images = [ Background4, Background3, Background1, Background2];


function Browse() {
  return (
    <HeroSlider
      slidingAnimation="left-to-right"
      orientation="horizontal"
      initialSlide={1}
      onBeforeChange={(previousSlide, nextSlide) =>
        console.log('onbeforechange', previousSlide, nextSlide)
      }
      onChange={(nextSlide) => console.log('onChange', nextSlide)}
      onAfterChange={(nextSlide) => console.log('onafterchange', nextSlide)}
      style={{ backgroundColor: 'rgba(0,0,0,0.33)', maxHeight: '300px' }}
      settings={{
        slidingDuration: 250,
        slidingDelay: 100,
        shouldAutoplay: true,
        shouldDisplayButtons: true,
        autoplayDuration: 5000,

      }}
    >
      <Grid container spacing={2}>
        {images.map((image) => (
          <Grid item xs={12} key={image} >
            <Item>
            <Typography variant="h2" sx={{ pt: 5 }} >Space-Info : </Typography>
            <Typography variant="h6" sx={{ width: "50%", m: "auto", mt: 5 }} >Space-Info Offre You Products Like Laptops, Desktops, Phones, ...
              We Have The Best Quality & The Lowest Prices, also you can make orders from home without Taxes.
            </Typography>
              <Slide
                background={{
                  backgroundImage: `url(${image})`,
                  backgroundAttachment: 'fixed',
                  backgroundSize: 'cover',
                }}
              />
            </Item>
          </Grid>
        ))}
      </Grid >
    </HeroSlider>
  );
}

export default Browse;
