import React from 'react'
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { SHOP_ACTIONS, ShoppingState } from '../../context/ShoppingContext';
import { useLocation } from 'react-router-dom';
import { Button, Container, Divider, Grid } from '@mui/material';
import { generatePhoto } from '../../utils/utilFunctions';
import DeleteIcon from '@mui/icons-material/Delete';
import "./style.css"

function Card() {
    const { shopsState, DispatchShop } = ShoppingState();
    const location = useLocation();
    const productID = location.pathname.split("/")[2];
    const Product = shopsState.allProducts.filter(Product => Product.data._id === productID)
    console.log(Product)


    return (

            <Grid component={Container} container spacing={3}  sx={{ m: "auto" ,mt: 5, p:1, border :"1px solid gray"}} >

                {Product.map((item) => (
                    <>
                        <Grid xs={4} >
                            <img id='imageProd' src={generatePhoto(item.data.type)} />
                        </Grid>

                        <Grid xs={6}>
                            <p>Name : {item.data.name}</p>
                            <p>Price : {item.data.price}</p>
                            <p>Type : {item.data.type}</p>
                            <p>Categorie : {item.data.categorie}</p>
                        </Grid>
                        <Grid xs={2}>
                            <p>Quantity : {item.Quantity}</p>
                            <p style={{ display: 'flex', alignItems: 'center' }}>
                                Delete :
                                <DeleteIcon sx={{ ml: 1, '&:hover': { cursor: "pointer", color: "red" } }} onClick={() => DispatchShop({ type: SHOP_ACTIONS.DELETE_FROM_CARD, payload: Product.data })} />
                            </p>
                            <Button variant='contained' color='success'>
                                Buy Product
                            </Button>
                        </Grid>
                    </>
                ))}
            </Grid>
    )
}

export default Card




