import React, { useEffect, useState } from 'react';
import AppBar from '@mui/material/AppBar';
import { Box, Toolbar, IconButton, Typography, Menu, Container, Avatar, Button, MenuItem } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { Link, useNavigate } from 'react-router-dom';

import computerLogo from '../../img/logo.png';
import SearchInput from '../search/Search';
import { AUTH_ACTIONS, AuthState } from '../../context/AuthContext';
import Basket from './basket/Basket';
import './style.css';
import NavLinks from './navLinks/NavLinks';


const settings = ['Profile', 'Account', 'Dashboard', 'Logout'];

function Header() {
    const navigate = useNavigate()
    const { user, authDispatch } = AuthState();
    useEffect(() => {
        if (!user) {
            navigate('/login');
        }
    }, [])
    return (
        <AppBar position="static" >
            <Container maxWidth="xl" className="appBar">
                <Toolbar disableGutters>
                    <Toolbar>
                        <Avatar component={Link} to="/" src={computerLogo} alt="IMG BOOKING" id="bookingLogo" />
                        <Typography
                            variant="h6"
                            noWrap
                            component={Link}
                            to="/"
                            sx={{
                                ml: 2,
                                mr: 2,
                                display: { sx: "none", md: 'flex' },
                                fontFamily: 'monospace',
                                fontWeight: 700,
                                letterSpacing: '.3rem',
                                color: 'inherit',
                                textDecoration: 'none',
                            }}
                        >
                            SpaceInfo
                        </Typography>
                    </Toolbar>

                    <NavLinks />

                    <Basket />

                    <Box sx={{ mt: 2, display: { xs: 'none', md: 'none', lg:"flex" } }}>

                        {user ?
                            <React.Fragment>
                                <Button component={Link} to='/' color="error" variant='outlined' onClick={() => authDispatch({ type: AUTH_ACTIONS.LOGOUT })} sx={{ fontWeight: "bold" }} >Logout</Button>
                                <Button component={Link} to="/profile" color="success" variant='outlined' sx={{ fontWeight: "bold", ml: 2 }} >Profile</Button>

                            </React.Fragment>

                            :
                            <React.Fragment>
                                <Button variant='contained' color="primary" onClick={() => navigate("/login")} >Login</Button>
                                <Button variant='outlined' color="primary" onClick={() => navigate("/register")} sx={{ ml: 2 }}>Register</Button>
                            </React.Fragment>
                        }
                    </Box>

                </Toolbar>
                <SearchInput />
            </Container>
        </AppBar>
    );
}
export default Header;
