import React, { useRef, useState, useEffect } from 'react'
import { Box, TextField, Container, Typography, Button } from '@mui/material'
import useFetch from '../../hooks/useFetch';
import Loading from '../../components/Loading/Loading'
import axios from 'axios';
import { CONTACT_ACTIONS, ContactState } from '../../context/ContactContext';

function Contact() {
    const [contact, setContact] = useState("");
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);

    //const {ContState, userID} = ContactState();
    const opinionRef = useRef(null);
    const errorDescription = useRef(null);
    const userID = JSON.parse(localStorage.getItem('CurrentUser'))._id;


    useEffect(() => {
        const fetchComments = async () => {
            setLoading(true)
            const response = await axios.get(`/contacts/${userID}`);
            setData(response.data);
            setLoading(false)
        };
        fetchComments();
    }, []);


    const handleADDContact = async (event) => {
        event.preventDefault();
        const message = opinionRef.current.value;
        console.log(opinionRef.current.value);
        if (message.length < 8) {
            return errorDescription.current.innerHTML = "Your Contact Message Must Contain at least 8 Caracters.";
        } else {
            errorDescription.current.innerHTML = "";
        }

        //DispatchContact({type:CONTACT_ACTIONS.START})

        try {
            const savedContact = await axios.post(`/contacts/${userID}`, { contact_message: contact });
            setData([...data, savedContact])

        } catch (error) {
            console.log(error.response.data.message)
        }
    }

    return (
        <Box sx={{ bgcolor: "silver", pt: 5 }}>
            <Container
                sx={{
                    '& .MuiTextField-root': { m: 1, width: { xs: '25ch', md: '50ch' } },
                    pb: 5,

                    width: "50%", m: 'auto'
                }}
            >
                <Box component="form" onSubmit={handleADDContact}>
                    <Typography variant='h6' sx={{ m: 1 }}>Contact us :</Typography>
                    <TextField
                        label="Contact us"
                        multiline
                        rows={5}
                        onChange={(e) => setContact(e.target.value)}
                        inputRef={opinionRef}
                    />
                    <Typography varaint='caption' ref={errorDescription} sx={{ color: "red", m: 1 }} ></Typography>
                    <Button
                        type="submit"
                        variant="contained"
                        color="primary"
                        sx={{ m: 2 }}
                    >
                        add contact
                    </Button>
                </Box>
            </Container>
            <Box disabled={loading} sx={{ border: "1px solid back", width: "60%", m: "auto", pb: 10, }}>
                {loading ?
                    <Loading />
                    :
                    <>
                        {data.map((item, index) => (
                            <Box sx={{ color: "purple", border: "1px solid purple", m: 2, p: 2 }} key={index}>
                                <span>Contact N°{index + 1} : </span>
                                <Typography variant="h6" sx={{ color: "rgba(0,0,0,0.8)" }}>{item.contact_message}</Typography>
                                <Button variant='outlined' color="error" sx={{ mt: 2 }}>DELETE</Button>
                                <Button variant='outlined' color="success" sx={{ mt: 2, ml: 5 }}>UPDATE</Button>
                            </Box>
                        ))}
                    </>
                }
            </Box>
        </Box>
    )
}

export default Contact
