import React, { useState } from 'react'
import { ThemeProvider, createTheme, styled } from '@mui/material/styles';
import { Switch, Grid, Paper, Box, Typography } from '@mui/material';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import DraftsIcon from '@mui/icons-material/Drafts';
import PreviewIcon from '@mui/icons-material/Preview';
import ContactMailIcon from '@mui/icons-material/ContactMail';
import AccountBoxIcon from '@mui/icons-material/AccountBox';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#F7F7F7',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.mode === 'dark' ? '#F7F7F7' : '#000',
  ...theme.typography.body2,
}));

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#f50057',
    },
  },
});

function Profile() {
  const [mode, setMode] = useState('light');

  const toggleMode = () => {
    setMode(mode === 'light' ? 'dark' : 'light');
  };

  const [open, setOpen] = React.useState(true);

  const handleClick = () => {
    setOpen(!open);
  };
  return (
    <ThemeProvider theme={{ ...theme, palette: { ...theme.palette, mode } }} >
      <Box sx={{ width: "20%", m: "auto" }}>
        <Switch
          edge="end"
          onChange={toggleMode}

          inputProps={{
            'aria-labelledby': 'switch-list-label-wifi',
          }}
          aria-label='ssss'
        />
        <span style={{ fontWeight: "bold" }}>{mode === 'light' ? 'dark' : 'light'}</span>
      </Box>

      <Grid container spacing={2} >
        <Grid item xs={4} >

          <Item>
            <List
              sx={{ width: '100%', maxWidth: 360}}
              component="nav"
              aria-labelledby="nested-list-subheader"

            >
              <Typography variant='h6'>{JSON.parse(localStorage.getItem('CurrentUser')).username}</Typography>
              <ListItemButton sx={{mt:5}}>
                <ListItemIcon>
                  <AccountBoxIcon sx={{color : mode === 'light' ? '#1A2027' : '#F7F7F7'}} />
                </ListItemIcon>
                <ListItemText primary="About Your Profile" />
              </ListItemButton>
              <ListItemButton sx={{mt:5}}>
                <ListItemIcon>
                  <PreviewIcon sx={{color : mode === 'light' ? '#1A2027' : '#F7F7F7'}} />
                </ListItemIcon>
                <ListItemText primary="Your Reviews" />
              </ListItemButton>
              <ListItemButton sx={{mt:5, mb:10}}>
                <ListItemIcon>
                  <ContactMailIcon sx={{color : mode === 'light' ? '#1A2027' : '#F7F7F7'}}/>
                </ListItemIcon>
                <ListItemText primary="Your Contacts"  />
              </ListItemButton>
            </List>
          </Item>
        </Grid>
        <Grid item xs={8} >
          <Item>
            <Typography variant="h3" sx={{color: mode === 'light' ? 'purple' : 'silver', frontWeight:"bold"}} >
            {JSON.parse(localStorage.getItem('CurrentUser')).first_name} {JSON.parse(localStorage.getItem('CurrentUser')).last_name}

            </Typography>
          </Item>
        </Grid>
      </Grid>


    </ThemeProvider>
  )
}

export default Profile
