import React, { Children, createContext, useContext } from 'react'
import { FilterNavReducer, UpdateCheckboxsReducer } from './Reducers';
const FilterSideContext = createContext();

export const ACTIONS_NAV = {

  TYPE : {
    ASUS : "asus",
    LENOVO : "lenovo",
    MACOS : "macos",
    DELL : "dell"
  },

  CATEGORIE : {
    LAPTOP : "laptop",
    DESKTOP : "desktop",
    PHONE : "phone"
  },

}

const initState = {
  computers : [],
  filtredComputers : [],
}

const initialState = {
  categories: {
    laptop: false,
    desktop: false,
  },
  types: {
    asus: false,
    lenovo: false,
    mac: false,
    dell: false,
  },
};

export default function FilterSidebarContext({Children}) {

  const [statenav, dispatchFilternav] = useReducer(FilterNavReducer , initState);
  const [checkboxstate, dispatchCheckbox] = useReducer(UpdateCheckboxsReducer ,initialState);

  function handleCategoryToggle(category) {
    dispatch({ type: 'TOGGLE_CATEGORY', payload: category });
  }

  function handleTypeToggle(type) {
    dispatch({ type: 'TOGGLE_TYPE', payload: type,checkboxstate, handleCategoryToggle, handleTypeToggle });
  }


  return (
    <FilterSideContext.Provider value={{statenav, dispatchFilternav}}>
        {Children}
    </FilterSideContext.Provider>
  )
}

export const  FilterSidebarState = ()=>{
    return useContext(FilterSideContext);
}
