import React, { useRef, useState } from 'react'
import { Box, TextField, Container, Typography, Button, FormControl } from '@mui/material'

function Contact() {
    const [contact, setContact] = useState("");

    const opinionRef = useRef(null);
    const errorDescription = useRef(null);

    const handleSubmit = (e) => {
      e.preventDefault();

      const message = opinionRef.current.value;

      if (message.length < 8) {
        return errorDescription.current.innerHTML = "Your Message Must Contain over 7 Caracters";
      }else{
        errorDescription.current.innerHTML= "";
      }

      const date = new Date();
      const newContact = {
        id: contact.length + 1,
        user_id: localStorage.getItem('getID'),
        content: message,
        published_date: `${date.getDate()} / ${date.getMonth()} / ${date.getFullYear()}`
      }

      setContact([...contact, newContact])

    }

    return (
        <Box sx={{ bgcolor: "silver", }}>
            <Container
                component="form"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: { xs: '25ch', md: '50ch' } },

                    pb: 20,
                    pt: 10,
                    width: "60%", m: 'auto'
                }}
                noValidate
                autoComplete="off"
            >


                <FormControl  >
                    <Typography variant='h6'>Contact us :</Typography>
                    <TextField
                        label="Contact us"
                        multiline
                        rows={5}
                        onChange={(e) => setContact(e.target.value)}
                        inputRef={opinionRef}
                    />
                    <Typography varaint='caption' ref={errorDescription} ></Typography>
                    <Button variant='contained' sx={{ m: 1, display: "block" }} onClick={handleSubmit}>Add Contact</Button>
                </FormControl>

            </Container>
        </Box>
    )
}

export default Contact
