import React from 'react'
import { Typography, Grid, CardMedia, CardContent, CardActions,  Card, Button} from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import imagePa from '../../img/products/laptops/laptop1.jpeg';
import { useNavigate } from 'react-router-dom';
import './style.css';

function SingleProduct({item}) {
    const navigate = useNavigate();
    const NavigateToDetails = () => {
        navigate(`/product_details/${item._id}`,
            {
                product_infos: {
                    post_id: 1,
                    post_name: 'Lorem Ipsum',
                    post_description: "Lorem Ipsum is simply dummy text of the printing and typesetting industry."
                }
            });
    }
    return (
        <Grid item  xs={12} sm={6} md={4}>
            <Card
                sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                id="CardItem"
            >
                <CardMedia
                    component="img"
                    sx={{ height: 200 }}
                    image={imagePa}
                    alt="random"
                />
                <CardContent sx={{ flexGrow: 1}}>
                    <Typography gutterBottom variant="h5"  sx={{mb:1}}>
                     {item.name.substring(0, 30)}...
                    </Typography>

                    <Typography gutterBottom variant="caption"  sx={{mb:1}}>
                       [ {item.reference} ] 
                    </Typography>
                    <Typography gutterBottom component="h2"  color="red"  sx={{mb:1}}>
                       {item.type}
                    </Typography>
                    <Typography component="h2"  sx={{ color: item.inStock ? "green" : "red"  }} >
                       {item.inStock ? "In Stock" : "Unavailable"} 
                    </Typography>
                    <Typography gutterBottom  color="red" component="h2" sx={{mb:1}}>
                        {item.price}, 000 DT 
                    </Typography>
                </CardContent>
                <CardActions sx={{bgcolor:"black"}}>
                    <Button size="small"  variant='outlined' id="btnprod"><ShoppingCartIcon />Add Product</Button>
                    <Button size="small" variant='outlined' id="btnprod"  onClick={NavigateToDetails()}>Show Product</Button>
                </CardActions>
            </Card>
        </Grid>
    )
}

export default SingleProduct
