import React, { useState } from 'react'
import { Typography, Box, Grid, Button } from '@mui/material';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { ACTIONS } from '../../context/FilterListContext';
import { FilterListState } from '../../context/FilterListContext';

function Filter() {
    const [sort, setSort] = useState('ALL');
    const {computers, dispatchFilter} = FilterListState();

    const handleChange = (event) => {
        setSort(event.target.value);
    };

    return (
        <Box sx={{ bgcolor: "rgba(0,0,0,0.9)", borderRadius: 2, width: "70%", m: "auto", p: 2, pl: 4 , display:"flex" }}>
            <Typography variant="h6" sx={{ color: "white" }}> Sort Computers : </Typography>

                <Select
                    labelId="demo-simple-select-filled-label"
                    id="demo-simple-select-filled"
                    value={sort}
                    sx={{ color: "yellow",bgcolor:"gray", borderRadius:2 , ml:"15%", fontWeight: "bold", fontSize: 17, width: 200, height: 40}}
                    onChange={handleChange}
                >
                    <MenuItem onClick={()=> dispatchFilter({type : ACTIONS.ALL, payload: computers })} >All</MenuItem>
                    <MenuItem onClick={()=> dispatchFilter({type : ACTIONS.PRICE_DESC})} >Price Descending</MenuItem>
                    <MenuItem onClick={()=> dispatchFilter({type : ACTIONS.PRICE_ASC})} >Price Acsending</MenuItem>
                    <MenuItem onClick={()=> dispatchFilter({type : ACTIONS.IN_STOCK})} >In Stock</MenuItem>
                </Select>

            <Button variant='contained' sx={{display:"inline", ml:"15%"}} onClick={()=> dispatchFilter({type : ACTIONS.DELETE_FILTER})} >Clear Filter</Button>
        </Box>
    )
}

export default Filter
