import React, { useState } from 'react'
import { Typography, Box, Grid, Button } from '@mui/material';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import { ACTIONS } from '../../context/FilterListContext';
import { FilterListState } from '../../context/FilterListContext';

function Filter() {
    const [sort, setSort] = useState('ALL COMPUTERS');
    const {computers, dispatchFilter} = FilterListState();

    const handleChange = (event) => {
        setSort(event.target.value);
    };
    console.log(computers)
    return (
        <Box sx={{ bgcolor: "rgba(0,0,0,0.9)", borderRadius: 2, width: "70%", m: "auto", p: 2, pl: 4 , display: {md: "flex"} }}>
            <Typography variant="h6" sx={{ color: "white", display: {xs:"none"}}}> Sort Computers : </Typography>

                <Select
                    labelId="demo-simple-select-filled-label"
                    id="demo-simple-select-filled"
                    value={sort}
                    sx={{ color: "yellow", mb:2, bgcolor:"gray", borderRadius:2 , ml:{md:"15%"}, fontWeight: "bold", fontSize: 17, width: 200, height: 40}}
                    onChange={handleChange}
                >
                    <MenuItem value = "ALL COMPUTERS" onClick={()=> dispatchFilter({type : ACTIONS.ALL, payload: computers })} >All</MenuItem>
                    <MenuItem value = "Price Descending"  onClick={()=> dispatchFilter({type : ACTIONS.PRICE_DESC})} >Price Descending</MenuItem>
                    <MenuItem value = "Price Acsending" onClick={()=> dispatchFilter({type : ACTIONS.PRICE_ASC})} >Price Acsending</MenuItem>
                    <MenuItem value = "In Stock" onClick={()=> dispatchFilter({type : ACTIONS.IN_STOCK})} >In Stock</MenuItem>
                </Select>

            <Button variant='contained' sx={{ml:"15%",display:"inline", height:{xs:30}}} onClick={()=> dispatchFilter({type : ACTIONS.DELETE_FILTER, payload: computers})} >Delete Filter</Button>
        </Box>
    )
}

export default Filter
