import React, {useState} from 'react'
import { Typography, Grid} from '@mui/material';
import List from '@mui/material/List';
import Checkbox from '@mui/material/Checkbox';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import { FilterProductState } from '../../context/filterProductContext';

function Filter() {
    const {
        filterProdDispatch,
        filterProdState: { byProducer, byCategorie, byPrice },
      } = FilterProductState();

    const [price, setPrice] = useState("");

    return (
        <Grid item xs={3} sx={{ bgcolor: "rgba(0,0,0,0.2)", color: "black", pt: 10 }}>
            <List
                sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}
                component="nav"
                aria-labelledby="nested-list-subheader"
            >
                <Typography sx={{ color: "white", bgcolor: "#B22222", fontWeight: "bold", letterSpacing: 5, textAlign: 'center', py: 1 }}>
                    Filter Products :
                </Typography>

                <h3>Producer : </h3>
                <FormGroup sx={{ pl: 4 }}>
                    <FormControlLabel control={<Checkbox  checked={byProducer} />} label="ASUS" />
                    <FormControlLabel control={<Checkbox   checked={byProducer}/>} label="LENOVO" />
                    <FormControlLabel control={<Checkbox   checked={byProducer}/>} label="MAC OS" />
                    <FormControlLabel control={<Checkbox   checked={byProducer}/>} label="DELL" />
                </FormGroup>

                <h3>Category : </h3>
                <FormGroup sx={{ pl: 4 }}>
                    <FormControlLabel control={<Checkbox onChange={() => filterProdDispatch({ type: "categorie", payload: "Laptop" })} />} label="Laptop" />
                    <FormControlLabel control={<Checkbox onChange={() => filterProdDispatch({ type: "categorie", payload: "Desktop" })} />} label="Desktop" />
                </FormGroup>

                <h3>price : </h3>
                <FormGroup sx={{ pl: 4, mb: "30px" }}>
                    <input onChange={(e) => setPrice(e.target.value)} type="text" />
                    <button onChange={() => filterProdDispatch({ type: "price", payload: price })} >filter</button>
                </FormGroup>

            </List>


        </Grid>
    )
}

export default Filter
