import React, { useEffect } from 'react'

import { SHOP_ACTIONS, ShoppingState } from '../../context/ShoppingContext';
import { useLocation} from 'react-router-dom';

import "./style.css"
import Cart from '../../components/cart/Cart';

function Card() {
    const { shopsState, DispatchShop } = ShoppingState();
    const location = useLocation();

    let productID = location.pathname.split("/")[2];
    useEffect(() => {
        if (!productID) {
            navigate('/products');
        }
    }, []);

    console.log(productID)

    const Product = shopsState.allProducts.filter(Product => Product.data._id === productID)
    const deleteProduct = (Product) => {
        DispatchShop({ type: SHOP_ACTIONS.DELETE_FROM_CARD, payload: Product.data })
        navigate("/cards");
    }
    return (
        <>
            {Product.map((item) => (
                <Cart item = {item} deleteProduct = {deleteProduct} />
            ))}
        </>
    )
}

export default Card




