const ACTIONS ={
    PRICE : "price",
    PRODUCER : "categorie",
    CATEGORY : "producer"
}

const computerReducer = (state, action) => {
    switch (action.type) {
      case "SET_COMPUTERS":
        return {
          ...state,
          computers: action.payload,
          filteredComputers: action.payload,
        };
      case "FILTER_COMPUTERS":
        const { filters } = state;
        const filteredByType = filters.type.asus
          ? state.computers.filter((comp) => comp.type === "Asus")
          : filters.type.lenovo
          ? state.computers.filter((comp) => comp.type === "Lenovo")
          : filters.type.macos
          ? state.computers.filter((comp) => comp.type === "MacOS")
          : state.computers;
        const filteredByCategory = filters.category.laptop
          ? filteredByType.filter((comp) => comp.category === "Laptop")
          : filters.category.desktop
          ? filteredByType.filter((comp) => comp.category === "Desktop")
          : filteredByType;
        const filteredByPrice = filteredByCategory.filter(
          (comp) => comp.price <= filters.price
        );
        return {
          ...state,
          filteredComputers: filteredByPrice,
        };
      case "UPDATE_FILTERS":
        return {
          ...state,
          filters: {
            ...state.filters,
            [action.filterType]: {
              ...state.filters[action.filterType],
              [action.filterName]: action.filterValue,
            },
          },
        };
      default:
        return state;
    }
  };
