import React, { useState, useContext } from 'react';
import { Typography, Box, Container, Grid, Button } from '@mui/material';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import useFetch from "../../hooks/useFetch.js";
import ProductsList from '../../components/productsList/ProductsList';
import Loading from '../../components/Loading/Loading';

import "./style.css";

function Products() {
  const { data, loading, error } = useFetch("/products");

  const [sort, setSort] = useState('All');

  const handleChange = (event) => {
    setSort(event.target.value);
  };

  return (
    <React.Fragment>
      <>
        {loading ?

          <Loading />
          :
          <Box sx={{bgcolor:"silver"}}>

            <Container sx={{pt:5}} >

              <Box sx={{bgcolor:"rgba(0,0,0,0.9)", borderRadius:2 , width:"80%", p:1}}>
              <Typography variant="h6" sx={{color : "white"}}> Sort Computers : </Typography>

                <FormControl variant="filled" sx={{bgcolor:'gray', borderRadius: 2 }}>

                  <Select
                    labelId="demo-simple-select-filled-label"
                    id="demo-simple-select-filled"
                    value={sort}
                    sx={{color: "yellow", fontWeight:"bold", fontSize:17}}
                    onChange={handleChange}
                  >
                    <MenuItem value="All">All</MenuItem>
                    <MenuItem value={10}>Price Descending</MenuItem>
                    <MenuItem value={20}>Price Acsending</MenuItem>
                    <MenuItem value={30}>In Stock</MenuItem>

                  </Select>
                </FormControl>
              </Box>
              <ProductsList data={data} />

            </Container>

          </Box>
        }

      </>
    </React.Fragment>
  )
}

export default Products
