import React, { useRef, useState } from 'react'
import { Typography, TextField, Grid, Container, Paper, Button } from '@mui/material';
import { RegisterState } from '../../context/RegisterContext';
import { Register_Verification } from '../../utils/utilFunctions';



const GridOptions = [
    { name: "username", lab: "Username" },
    { name: "email", lab: "Email" },
    { name: "password", lab: "Password"},
    { name: "first_name", lab: "First name" },
    { name: "last_name", lab: "Last name" },
    { name: "address", lab: "address" },
    { name: "city", lab: "City"},
    { name: "state", lab: "State" },
    { name: "country", lab: "Country" },
    { name: "phone", lab: "Phone"}
]

function Register() {
    const {FieldState, DispatchField} = RegisterState();

    const refs = [
        errorUsername = useRef(null),
        errorEmail = useRef(null),
        errorFirstn = useRef(null),
        errorLastn = useRef(null),
        errorAddress = useRef(null),
        errorCity = useRef(null),
        errorState = useRef(null),
        errorCountry = useRef(null),
        errorPhone = useRef(null)
    ];

    /*
    const fetch_error = Register_Verification();
    if(fetch_error){
        return
    }*/

    return (
        <Container component="main" maxWidth="sm" sx={{ my: 8 }}>
            <Paper variant="outlined" sx={{ my: { xs: 3, md: 6 }, p: { xs: 2, md: 3 }, bgcolor: '#F8F8F8' }}>
                    <Typography variant="h6" gutterBottom sx={{ mb: 5 }}>
                        Register :
                    </Typography>
                    <Grid container spacing={3}>
                        {GridOptions.map((option, index) => (
                            <Grid item xs={12} sm={6} sx={{mb:1}} key={option.name}>
                                <TextField
                                    required
                                    inputRef={refs(index)}
                                    id={option.name}
                                    name={option.name}
                                    label={option.lab}
                                    fullWidth
                                    variant="standard"
                                    onChange = {(e)=>{DispatchField({type : option.name, payload : e.target.value })}}
                                />
                            </Grid>
                        ))
                        }
                        <Button variant='contained' sx={{width:"30%", m:'auto', mt:5}}>Register</Button>
                    </Grid>
            </Paper>
        </Container>
    )
}

export default Register
