const ACTIONS ={
    PRICE : "price",
    PRODUCER : "categorie",
    CATEGORY : "producer"
}

export function computerReducer(state, action) {
  switch (action.type) {
    case 'SET_COMPUTER_DATA':
      return { ...state, computers: action.payload };
    case 'TOGGLE_CATEGORY':
      return {
        ...state,
        categories: {
          ...state.categories,
          [action.payload]: !state.categories[action.payload],
        },

      };
    case 'TOGGLE_TYPE':
      const computersss = computers.filter(comp => comp.type == action.payload)
      return {
        computers : {...computersss},
        ...state,
        types: {
          ...state.types,
          [action.payload]: !state.types[action.payload],
        },

      };
    default:
      return state;
  }
}
