import React, { createContext, useContext, useReducer, useEffect } from 'react'
import { FilterListReducer } from './Reducers';
const FiltreContext = createContext();

export const ACTIONS = {
    PRICE_ASCEND : "price ascending",
    PRICE_DESCENDING : "price descending",
    IN_STOCK : "in stock" 
}

const initState = {
    computers : [],
    filter : {

    }
}

export default function FilterListContextProvider({ children }) {
    const [state, dispatchFilter] = useReducer(FilterListReducer, initState);

    useEffect(() => {
        fetch('/products')
            .then((response) => response.json())
            .then((data) => {
                dispatchFilter({ type: 'SET_COMPUTERS', payload: data });
            });
    }, []);

    return (
        <FiltreContext.Provider value={{state, dispatchFilter}}>
            {children}
        </FiltreContext.Provider>
    )
}

export const FilterListState = () => {
    return useContext(FiltreContext);
}
