import React from 'react'
import { Box, Button, Container, Typography } from '@mui/material';
import { styled } from '@mui/material/styles';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import imagePa from '../../img/products/laptops/laptop3.jpg';
import useFetch from '../../hooks/useFetch';
import { useLocation } from 'react-router-dom';
import './style.css';

const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));

function ProductDetails() {
    const location = useLocation();
    const prodID = location.pathname.split("/")[2];
    const { data, loading, error } = useFetch(`/products//${prodID}`);
    console.log(data);
    return (
        <Container sx={{ pt: 10 }}>

            <Grid container spacing={3} sx={{bgcolor:"#e7e7e7", py:10}} >
                <Grid item xs  >
                    <Box
                        component="img"
                        sx={{
                            height: 333,
                            width: 350,
                            maxHeight: { xs: 233, md: 567 },
                            maxWidth: { xs: 350, md: 350 },
                        }}
                        alt="The house from the offer."
                        src={imagePa}
                        id="imageProd"
                    />
                </Grid>
                <Grid item xs={6}>

                    <Typography variant="h6" sx={{color:"blue"}}>
                        {data.name}
                    </Typography>

                    <Typography variant="h6" sx={{color : data.inStock ? "green" :"red", my:2 }}>
                        {data.inStock ? " In Stock" : "Not Available"}
                    </Typography>

                    <Typography variant="caption" sx={{color:"gray"}}>
                       Reference#: {data.reference}
                    </Typography>

                    <Typography variant="body2" sx={{lineHeight:1.8, my:2}}>
                        {data.description}
                    </Typography>
                </Grid>
                <Grid item xs sx={{bgcolor:"#eeeeee"}}>
                    <Typography variant="h5" sx={{ color:'red', my:2}}>
                        {data.price}, 000 DT
                    </Typography>
                    <Typography variant="h6" sx={{ my:2}}>
                        Quantity : <input type="text" style={{width:40}}></input>
                    </Typography>
                    <Button variant='contained' color='success'>
                        Add to Cart
                    </Button>
                </Grid>
            </Grid>

        </Container>
    )
}

export default ProductDetails
