import React from 'react';
import HeroSlider, { Slide } from 'hero-slider';
import { Grid,IconButton } from '@mui/material';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import Background1 from '../../img/wallpaper_lap.jpeg';
import Background2 from '../../img/download.jpeg';
import Background3 from '../../img/laptop-transparent-png-pictures-icons-and-png-40.png';
import Background4 from '../../img/murjp1nk4lp1idlt.jpg';


const images = [Background1, Background2, Background3, Background4];
const [currentSlide, setCurrentSlide] = useState(0);
const maxSlide = images.length - 1;
const Browse = () => {
  const handlePreviousSlide = () => {
    setCurrentSlide(currentSlide === 0 ? maxSlide : currentSlide - 1);
  };

  const handleNextSlide = () => {
    setCurrentSlide(currentSlide === maxSlide ? 0 : currentSlide + 1);
  };
  return (
    <Grid container spacing={0}>
      <Grid item xs={1}>
        <IconButton onClick={handlePreviousSlide}>
          <ArrowBackIosIcon />
        </IconButton>
      </Grid>
      <Grid item xs={10}>
        <img src={images[currentSlide]} alt={`Slide ${currentSlide + 1}`} />
      </Grid>
      <Grid item xs={1}>
        <IconButton onClick={handleNextSlide}>
          <setCurrentSlide />
        </IconButton>
      </Grid>
    </Grid>
  );
};

export default Browse;
