import React, { useState } from 'react'
import { Box, FormLabel, Typography, FormControlLabel, Checkbox, FormGroup } from '@mui/material'
import List from '@mui/material/List';
import ListSubheader from '@mui/material/ListSubheader';

import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Collapse from '@mui/material/Collapse';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import StarBorder from '@mui/icons-material/StarBorder';


const categorie = ['Laptop', 'Desktop', "Phone"];
const type = ["ASUS", "LENOVO", "MAC OS", "DELL"];
const RAM = ['16 GO', '12 GO', "8 GO"];
const PROCESSOR = [" Intel Core i7", "Intel Core i5", " Intel Core i3"];
const GRAPHICS = ['Graphique Intégrée', 'Nvidia GeForce', " AMD Radeon"];
const disque = ['1 To HDD ', '1 To SSD', "500 Go SSD", " 500 Go HDD"];
const couleur = ['Gray', 'Black', "White", "Pink", "Red"];






function SidebarFilter() {
    const [checkedItems, setCheckedItems] = useState({});
    const [open, setOpen] = React.useState(true);

    const handleClick = () => {
      setOpen(!open);
    };

    const CheckboxList = () => {
        const [checkedItems, setCheckedItems] = useState({});
    }
    const handleChange = (event) => {
        setCheckedItems({ ...checkedItems, [event.target.name]: event.target.checked });
    };

    return (
        <Box sx={{ bgcolor: "rgba(0,0,0,0.9)", color: "white", overflow: "hidden", p: 2 }} >
            <Typography variant="h6">Filter Computers: </Typography>
            <List
                sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}
                component="nav"
                aria-labelledby="nested-list-subheader"
                subheader={
                    <ListSubheader component="div" id="nested-list-subheader">
                        Nested List Items
                    </ListSubheader>
                }
            >
                <ListItemButton onClick={handleClick}>
                    <ListItemIcon>
                        <InboxIcon />
                    </ListItemIcon>
                    <ListItemText primary="Inbox" />
                    {open ? <ExpandLess /> : <ExpandMore />}
                </ListItemButton>
                <Collapse in={open} timeout="auto" unmountOnExit>
                    <List component="div" disablePadding>
                        <ListItemButton sx={{ pl: 4 }}>
                            <ListItemIcon>
                                <StarBorder />
                            </ListItemIcon>
                            <ListItemText primary="Starred" />
                        </ListItemButton>
                    </List>
                </Collapse>
            </List>

            <Typography >Type: </Typography>
            <FormGroup >
                {type.map((option) => (
                    <FormControlLabel
                        key={option}
                        control={
                            <Checkbox
                                checked={checkedItems[option] || false}
                                onChange={handleChange}
                                name={option}
                                sx={{ color: 'white' }}
                            />
                        }
                        label={option}
                    />
                ))}
            </FormGroup>

        </Box >
    )
}

export default SidebarFilter
