import React, { useEffect } from 'react'
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { SHOP_ACTIONS, ShoppingState } from '../../context/ShoppingContext';
import { useLocation, useNavigate } from 'react-router-dom';
import { Button, Container, Divider, Grid } from '@mui/material';
import { generatePhoto } from '../../utils/utilFunctions';
import DeleteIcon from '@mui/icons-material/Delete';
import "./style.css"

function Card() {
    const { shopsState, DispatchShop } = ShoppingState();
    const location = useLocation();
    const navigate = useNavigate()
    let productID = location.pathname.split("/")[2];
    useEffect(() => {
        if (!productID) {
            navigate('/products');
        }
    }, []);

    console.log(productID)

    const Product = shopsState.allProducts.filter(Product => Product.data._id === productID)
    const deleteProduct = (item) => {
        DispatchShop({ type: SHOP_ACTIONS.DELETE_FROM_CARD, payload: item.data })
        navigate("/cards");
    }



    return (

        <>
            {Product.map((item) => (
                <Grid component={Container} key={item.data._id} container spacing={2} sx={{ m: "auto", mt: 5, p: 2, border: "1px solid gray" }} >

                    <Grid item xs={4} >
                        <img id='imageProd' src={generatePhoto(item.data.type)} />
                    </Grid>

                    <Grid item xs={6}>
                        <p>Name : {item.data.name}</p>
                        <p>Price : {item.data.price}</p>
                        <p>Type : {item.data.type}</p>
                        <p>Categorie : {item.data.categorie}</p>
                    </Grid>
                    <Grid item xs={2}>
                        <p >Quantity : {item.Quantity}</p>
                        <p style={{ display: 'flex', alignItems: 'center' }}>
                            Delete :
                            <DeleteIcon sx={{ ml: 1, '&:hover': { cursor: "pointer", color: "red" } }} onClick={() => deleteProduct(item)} />
                        </p>
                        <Button variant='contained' color='success'>
                            Buy Product
                        </Button>
                    </Grid>
                </Grid>
            ))}
        </>
    )
}

export default Card




