import React, { useRef, useState } from 'react'
import { Typography, TextField, Grid, Container, Paper, Button } from '@mui/material';
import { RegisterState } from '../../context/RegisterContext';
import { Register_Verification } from '../../utils/utilFunctions';



const GridOptions = [
    { name: "username", lab: "Username", ref: "errorUsername" },
    { name: "email", lab: "Email", ref: "errorEmail" },
    { name: "password", lab: "Password", ref: "errorPassword" },
    { name: "first_name", lab: "First name", ref: "errorFirstn" },
    { name: "last_name", lab: "Last name", ref: "errorLastn" },
    { name: "address", lab: "address", ref: "errorAddress" },
    { name: "city", lab: "City", ref: "errorCity" },
    { name: "state", lab: "State", ref: "errorState" },
    { name: "country", lab: "Country", ref: "errorCountry" },
    { name: "phone", lab: "Phone", ref: "errorPhone" }
]

function Register() {
    const {FieldState, DispatchField} = RegisterState();

    const refs = {
        errorUsername : useRef(null),

    }

    /*
    const fetch_error = Register_Verification();
    if(fetch_error){
        return
    }*/

    return (
        <Container component="main" maxWidth="sm" sx={{ my: 8 }}>
            <Paper variant="outlined" sx={{ my: { xs: 3, md: 6 }, p: { xs: 2, md: 3 }, bgcolor: '#F8F8F8' }}>
                    <Typography variant="h6" gutterBottom sx={{ mb: 5 }}>
                        Register :
                    </Typography>
                    <Grid container spacing={3}>
                        {GridOptions.map((option, index) => (
                            <Grid item xs={12} sm={6} sx={{mb:1}} key={option.name}>
                                <TextField
                                    required
                                    inputRef={refs(index)}
                                    id={option.name}
                                    name={option.name}
                                    label={option.lab}
                                    fullWidth
                                    variant="standard"
                                    onChange = {(e)=>{DispatchField({type : option.name, payload : e.target.value })}}
                                />
                            </Grid>
                        ))
                        }
                        <Button variant='contained' sx={{width:"30%", m:'auto', mt:5}}>Register</Button>
                    </Grid>
            </Paper>
        </Container>
    )
}

export default Register
