import React, { useState } from 'react'
import { Box, FormLabel, Typography, FormControlLabel, Checkbox, FormGroup, Button } from '@mui/material'

const categorie = ['Laptop', 'Desktop', "Phone"];
const type = ["ASUS", "LENOVO", "MAC OS"];
const RAM = ['16 GO', '12 GO', "8 GO"];
const PROCESSOR = [" Intel Core i7", "Intel Core i5", " Intel Core i3"];
const GRAPHICS = ['Graphique Intégrée', 'Nvidia GeForce', " AMD Radeon"];
const disque = ['1 To HDD ', '1 To SSD', "500 Go SSD", " 500 Go HDD"];

function SidebarFilter() {
    const [selectedCategory, setSelectedCategory] = useState('');
    const [selectedType, setSelectedType] = useState(false);

    function handleChangeCategorie(event) {
        setSelectedCategory(event.target.value);
    }

    function handleChangeType(event) {
        console.log(event.target.value)
        setSelectedType(event.target.value);
    }

    const handleClick = ()=>{

    }

    //checked={} onChange={}
    return (
        <Box sx={{ bgcolor: "rgba(0,0,0,0.9)", color: "white", overflow: "hidden", p: 2, pb: 50 }} >
            <Typography variant="h6">Filter Computers: </Typography>

            <Typography sx={{ color: "orange", fontWeight: "bold", mt: 2 }} >Categorie: </Typography>
            <div>

                <label>
                    <input type="checkbox" value="laptop" checked={selectedCategory === 'laptop'} onChange={handleChangeCategorie} />
                    Laptop
                </label><br />

                <label>
                    <input type="checkbox" value="checkbox" checked={selectedCategory === 'checkbox'} onChange={handleChangeCategorie} />
                    Checkbox
                </label><br />

                <label>
                    <input type="checkbox" value="phone" checked={selectedCategory === 'phone'} onChange={handleChangeCategorie} />
                    Phone
                </label><br />
            </div>

            <Typography sx={{ color: "orange", fontWeight: "bold", mt: 2 }} >Types: </Typography>

            <div>
                <label>
                    <input type="checkbox" value="asus" checked={selectedType === 'asus'} onChange={handleChangeType} />
                    ASUS
                </label><br />

                <label>
                    <input type="checkbox" value="lenovo" checked={selectedType === 'lenovo'} onChange={handleChangeType} />
                    LENOVO
                </label><br />

                <label>
                    <input type="checkbox" value="macos" checked={selectedType === 'macos'} onChange={handleChangeType} />
                    MAC OS
                </label><br />

                <label>
                    <input type="checkbox" value="dell" checked={selectedType === 'dell'} onChange={handleChangeType} />
                    DELL
                </label><br />

            </div>


            <Button variant='contained' sx= {{mt:2}} onClick={handleClick}>clear Filter</Button>


        </Box>
    )
}

export default SidebarFilter
