import React, { useState, useEffect } from 'react';
import { Typography, Box, Container, Grid, Paper, Divider } from '@mui/material';
import useFetch from "../../hooks/useFetch.js";
import ProductsList from '../../components/productsList/ProductsList';
import Loading from '../../components/Loading/Loading';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Collapse from '@mui/material/Collapse';
import StoreIcon from '@mui/icons-material/Store';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import Checkbox from '@mui/material/Checkbox';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';

import "./style.css";

const ItelList = ({ Logo, Name }) => {
  let init = false;
  Name == "Producer" ? init = true : init = false;
  const [open, setOpen] = React.useState(init);
  const handleClick = () => {
    setOpen(!open);
  };

  return (
    <>
      <ListItemButton onClick={handleClick} sx={{ mt: 3 }}>
        <ListItemText primary={Name} />
        {open ? <ExpandLess /> : <ExpandMore />}
      </ListItemButton>
      <Collapse in={open} timeout="auto" unmountOnExit>
        <FormGroup sx={{pl:4}}>

          {Name === 'Producer' &&
              <>
              <FormControlLabel control={<Checkbox defaultChecked />} label="ASUS" />
              <FormControlLabel control={<Checkbox />}  label="LENOVO" />
              </>
          }

          {Name === 'Price' &&
              <>
              <FormControlLabel control={<Checkbox defaultChecked />} label="150" />
              <FormControlLabel control={<Checkbox />}  label="250" />
              </>
          }
        </FormGroup>
      </Collapse>
      <Divider variant="middle" flexItem />
    </>
  )
}



function Products() {
  const { data, loading, error } = useFetch(`/products`);

  /*
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const url  ="/products"
  useEffect(()=>{
      const FetchApiData = async()=>{
      setLoading(true);
      console.log(loading)
      try{
          const res = await axios.get(url);
          setData(res.data);
      }catch(error){
          setError(error);
          console.log(error.message);
      }
      setLoading(false);
  };
  FetchApiData();
  }, [url])
  */

  return (
    <React.Fragment>
      <Grid container spacing={2}>
        <Grid item xs={3} sx={{ bgcolor: "rgba(0,0,0,0.2)", color: "black", pt: 10 }}>
          <List
            sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}
            component="nav"
            aria-labelledby="nested-list-subheader"
          >
            <Typography sx={{ color: "white", bgcolor: "#B22222", fontWeight: "bold", letterSpacing: 5, textAlign: 'center', py: 1 }}>
              Filter Products :
            </Typography>

            <ItelList Logo={StoreIcon} Name="Producer" />
            <ItelList Logo={StoreIcon} Name="Price" />
            <ItelList Logo={StoreIcon} Name="Availablity" />
            <ItelList Logo={StoreIcon} Name="Gamer" />
            <ItelList Logo={StoreIcon} Name="System" />
            <ItelList Logo={StoreIcon} Name="RAM" />
            <ItelList Logo={StoreIcon} Name="PROCESSOR" />
            <ItelList Logo={StoreIcon} Name="GRAPHICS" />
            <ItelList Logo={StoreIcon} Name="HARD DISK" />
            <ItelList Logo={StoreIcon} Name="COLOR" />
            <ItelList Logo={StoreIcon} Name="GARANTI" />
          </List>
        </Grid>
        <Grid item xs={9} sx={{ bgcolor: "rgba(0,0,0,0.2)", color: "black", pt: 10 }}>
          <>
            {loading ?

              <Loading />
              :
              <Box >

                <Container >
                  <Typography variant='h3' sx={{ textAlign: 'center' }}>Laptops : </Typography>
                  <Typography variant='h6' sx={{ textAlign: 'center', color: "purple" }}>we only have 20 laptops for sale</Typography>
                  <ProductsList data={data} />
                  <Typography variant='h3' sx={{ textAlign: 'center', mt: 5 }}>Desktops : </Typography>
                  <Typography variant='h6' sx={{ textAlign: 'center', color: "purple" }}>we only have 20 Desktops for sale</Typography>
                  <ProductsList data={data} />
                  <Typography variant='h3' sx={{ textAlign: 'center', mt: 5 }}>Phones : </Typography>
                  <Typography variant='h6' sx={{ textAlign: 'center', color: "purple" }}>we only have 20 Phones for sale</Typography>
                  <ProductsList data={data} />
                  <Typography variant='h3' sx={{ textAlign: 'center', mt: 5 }}>Other Products : </Typography>
                  <Typography variant='h6' sx={{ textAlign: 'center', color: "purple" }}>we only have 20 Products for sale</Typography>

                  <ProductsList data={data} />
                </Container>

              </Box>
            }

          </>
        </Grid>
      </Grid>

    </React.Fragment>
  )
}

export default Products
