import React, { useState, useReducer } from 'react'
import { Box, Typography,  Button } from '@mui/material'
import { FilterSidebarState } from '../../context/FilterSidebarContext';
import { FilterListState } from '../../context/FilterListContext';
import { ACTIONS } from '../../context/FilterListContext';
import './style.css';

function SidebarFilter() {
    const  { checkboxstate, handleCategoryToggle, handleTypeToggle} = FilterSidebarState()
    const { dispatchFilter } = FilterListState();
    console.log(checkboxstate)
    return (
        <Box sx={{ bgcolor: "rgba(0,0,0,0.9)", color: "white", overflow: "hidden", p: 2, pb: 50 }} >
            <Typography variant="h6">Filter Computers: </Typography>

            <Typography sx={{ color: "orange", fontWeight: "bold", mt: 2 }} >Categorie: </Typography>
            <label >
                <input type="checkbox" checked={checkboxstate.categories.laptop} onChange={() => handleCategoryToggle('laptop')} onClick={ ()=> dispatchFilter({type : ACTIONS.category.laptop, payload : { filter_name : "asus"  } }) }/>   
                Laptop
            </label><br />
            <label>
                <input type="checkbox" checked={checkboxstate.categories.desktop} onChange={() => handleCategoryToggle('desktop')} />
                Desktop
            </label><br />

            <label>
                <input type="checkbox" checked={checkboxstate.categories.phone} onChange={() => handleCategoryToggle('phone')} />
                Smartphone
            </label><br />

            <Typography sx={{ color: "orange", fontWeight: "bold", mt: 2 }} >Types: </Typography>
            <label>
                <input type="checkbox" checked={checkboxstate.types.asus} onChange={() => handleTypeToggle('asus')} />
                Asus
            </label><br />
            <label>
                <input type="checkbox" checked={checkboxstate.types.lenovo} onChange={() => handleTypeToggle('lenovo')} />
                Lenovo
            </label><br />
            <label>
                <input type="checkbox" checked={checkboxstate.types.mac} onChange={() => handleTypeToggle('mac')} />
                Mac OS
            </label><br />

            <label>
                <input type="checkbox" checked={checkboxstate.types.dell} onChange={() => handleTypeToggle('dell')} />
                Dell
            </label><br />

            <Button variant='contained' sx={{ mt: 2 }} >clear Filter</Button>


        </Box>
    )
}

export default SidebarFilter

