import React from 'react'

import { Typography, Grid, CardMedia, CardContent, CardActions,  Card, Button} from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import imagePa from '../../img/products/laptops/laptop1.jpeg';
import './style.css';

function SingleProduct({item}) {

    return (
        <Grid item  xs={12} sm={6} md={4}>
            <Card
                sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                id="CardItem"
            >
                <CardMedia
                    component="img"
                    sx={{ height: 200 }}
                    image={imagePa}
                    alt="random"
                />
                <CardContent sx={{ flexGrow: 1}}>
                    <Typography gutterBottom variant="h5" component="h2" sx={{mb:2}}>
                     {item.name.substring(0, 30)}...
                    </Typography>

                    <Typography gutterBottom variant="caption" component="h2" sx={{mb:2}}>
                       [ {item.reference} ] 
                    </Typography>
                    <Typography gutterBottom variant="h6" color="red" component="h2" sx={{mb:2}}>
                       {item.type}
                    </Typography>
                    <Typography variant="h6" sx={{ color: item.inStock ? "green" : "red"  }} >
                       {item.inStock ? "In Stock" : "Unavailable"} 
                    </Typography>
                </CardContent>
                <CardActions sx={{bgcolor:"black"}}>
                    <Button size="small" id="btnprod"></Button>
                    <Button size="small" id="btnprod">Edit</Button>
                </CardActions>
            </Card>
        </Grid>
    )
}

export default SingleProduct
