import React, { createContext, useReducer, useContext } from 'react'
import { computerReducer } from './Reducers';

const ComputerContext = createContext();


const initialState = {
    computers: [],
    filteredComputers: [],
    filters: {
        type: {
            asus: false,
            lenovo: false,
            macos: false,
        },
        category: {
            laptop: false,
            desktop: false,
        },
        price: "",
    },
};

export const ComputerContextProvider = ({ children }) => {


    const [state, dispatch] = useReducer(computerReducer, initialState);

    const setComputers = (computers) => {
        dispatch({ type: "SET_COMPUTERS", payload: computers });
    };

    const filterComputers = () => {
        dispatch({ type: "FILTER_COMPUTERS" });
    };

    return (
        <ComputerContext.Provider value={{ state, setComputers, filterComputers }}>
            {children}
        </ComputerContext.Provider>
    )
}

export const FilterComputersState = () => {
    return useContext(ComputerContext);
};
