import React, { useRef, useState } from 'react'
import { Box, TextField, Container, Typography, Button } from '@mui/material'

function Contact() {
    const [contact, setContact] = useState("");
    const contactref = useRef();
    const handleADDContact = () =>{
        if(contact === ""){
            contactref.current.value = "The Contact Message is Empty"
        }
    }
    return (
        <Box sx={{ bgcolor: "silver", }}>
            <Container
                component="form"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: {xs: '25ch', md: '50ch'}  },

                    pb: 20,
                    pt: 10,
                    width: "60%", m: 'auto'
                }}
                noValidate
                autoComplete="off"
            >
                <ipput ref={contactref} ></ipput>
                <Typography variant='h6'>Contact us :</Typography>
                <TextField
                    label="Contact us"
                    multiline
                    rows={5}
                    onChange={(e)=>setContact(e.target.value)}
                    ref={contactref}
                />
                <Button variant='contained' sx={{m:1, display:"block"}} onClick={handleADDContact}>Add Contact</Button>
            </Container>
        </Box>
    )
}

export default Contact
