import React, { createContext, useReducer, useContext } from 'react'
import useFetch from '../hooks/useFetch';

const FilterProductContext = createContext();


const { data, loading, error } = useFetch(`/products`);

const initialState = {
    computers: [],
    filteredComputers: [],
    filters: {
        type: {
            asus: false,
            lenovo: false,
            macos: false,
        },
        category: {
            laptop: false,
            desktop: false,
        },
        price: "",
    },
};

export const FilterProductsContextProvider = ({ children }) => {


    const [state, dispatch] = useReducer(computerReducer, initialState);

    const setComputers = (computers) => {
        dispatch({ type: "SET_COMPUTERS", payload: computers });
    };

    const filterComputers = () => {
        dispatch({ type: "FILTER_COMPUTERS" });
    };

    return (
        <FilterProductContext.Provider value={{ state, setComputers, filterComputers }}>
            {children}
        </FilterProductContext.Provider>
    )
}

export const FilterProductState = () => {
    return useContext(FilterProductContext);
};
