import React from 'react'
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Grid from '@mui/material/Grid';
import { Typography } from '@mui/material';
import imagePa from '../../img/products/laptops/laptop1.jpeg';
import './style.css';

function SingleProduct({item}) {

    return (
        <Grid item  xs={12} sm={6} md={4}>
            <Card
                sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                id="CardItem"
            >
                <CardMedia
                    component="img"
                    sx={{ height: 200 }}
                    image={imagePa}
                    alt="random"
                />
                <CardContent sx={{ flexGrow: 1}}>
                    <Typography gutterBottom variant="h5" component="h2" sx={{mb:2}}>
                     {item.name.substring(0, 30)}...
                    </Typography>
                    <Typography>
                        {item.description.substring(0, 100)} .....

                    </Typography>
                </CardContent>
                <CardActions sx={{bgcolor:"black"}}>
                    <Button size="small" id="btnprod">View</Button>
                    <Button size="small" id="btnprod">Edit</Button>
                </CardActions>
            </Card>
        </Grid>
    )
}

export default SingleProduct
