import React, { useState } from 'react'
import { Box, FormLabel, Typography, FormControlLabel, Checkbox, FormGroup } from '@mui/material'

const categorie = ['Laptop', 'Desktop', "Phone"];
const type = ["ASUS", "LENOVO", "MAC OS"];
const RAM = ['16 GO', '12 GO', "8 GO"];
const PROCESSOR = [" Intel Core i7", "Intel Core i5", " Intel Core i3"];
const GRAPHICS = ['Graphique Intégrée', 'Nvidia GeForce', " AMD Radeon"];
const disque = ['1 To HDD ', '1 To SSD', "500 Go SSD", " 500 Go HDD"];

function SidebarFilter() {
    const [checkedItem, setCheckedItem] = useState(false);

    const handleChange = (event) => {
        console.log(event.target.checked)
        if(event.target.checked === true){
            setCheckedItem();
        }

    };

    return (
        <Box sx={{ bgcolor: "rgba(0,0,0,0.9)", color: "white", overflow: "hidden", p: 2, pb: 50 }} >
            <Typography variant="h6">Filter Computers: </Typography>

                <Typography sx={{ color: "orange", fontWeight: "bold", mt: 2 }} >Categorie: </Typography>

                <label>
                    <input type="checkbox" />
                    Laptop
                </label>
                <label>
                    <input type="checkbox" />
                    Desktop
                </label>

                <Typography sx={{ color: "orange", fontWeight: "bold", mt: 2 }} >Types: </Typography>

                <label>
                    <input type="checkbox"  />
                    Asus
                </label>
                <label>
                    <input type="checkbox"  />
                    Lenovo
                </label>
                <label>
                    <input type="checkbox"  />
                    Mac OS
                </label>


                <Typography sx={{ color: "orange", fontWeight: "bold", mt: 3 }} >Type: </Typography>




        </Box>
    )
}

export default SidebarFilter
