import axios from "axios";

import "./style.css";

function Products() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  useEffect(()=>{
      const FetchApiData = async()=>{
      setLoading(true);
      try{
          const res = await axios.get(url).then(res => res.json());
          console.log(res.data)
          setData(res.data);
      }catch(error){
          setError(error);
      }
      setLoading(false);
  };
  FetchApiData();

