import React from 'react';
import { Box, Container, Grid, Button } from '@mui/material';
import ProductsList from '../../components/productsList/ProductsList';
import Loading from '../../components/Loading/Loading';
import Filter from '../../components/filter/Filter.js';
import { FilterListState } from '../../context/FilterListContext.js';
import "./style.css";
import SidebarFilter from '../../components/filterSidebar/filterSidebar';

function Products() {
  //const { data, loading, error } = useFetch("/products");
  const { state, loading, error } = FilterListState();
  console.log(loading)
  return (
    <Grid container spacing={2}>

      <Grid item xs={3}>
        <SidebarFilter />
      </Grid>



      <Grid item xs={9}>

        <Box sx={{ bgcolor: "silver" }}>

          <Container sx={{ pt: 5 }} >
            <Filter />
            {loading ?

              <Loading />
              :

              <ProductsList data={state.computers} />
            }
            </Container>

        </Box>
      </Grid>





    </Grid>
  )
}

export default Products
