import React, { useState, useReducer } from 'react'
import { Box, Typography, Button } from '@mui/material'
import { FilterSidebarState } from '../../context/FilterSidebarContext';
import { FilterListState } from '../../context/FilterListContext';
import { ACTIONS } from '../../context/FilterListContext';
import './style.css';

function SidebarFilter() {
    const { productDispatch, productState: { byStock, byFastDelivery, sort, byRating } } = FilterSidebarState()

    return (
        <div className="filters">
            <span className="title">Filter Products : </span>

            <Typography sx={{ color: "orange", fontWeight: "bold", mt: 2 }} >Categories: </Typography>

            <label>
                <input type="radio" name="group1"
                    checked={sort === "lowToHigh" ? true : false}
                    onChange={() =>
                        productDispatch({
                            type: "SORT_BY_PRICE",
                            payload: "lowToHigh",
                        })
                    } />
                Laptop
            </label><br />


            <label>
                <input type="radio" name="group1"
                    checked={sort === "lowToHigh" ? true : false}
                    onChange={() =>
                        productDispatch({
                            type: "SORT_BY_PRICE",
                            payload: "highToLow",
                        })
                    }/>
                Desktop
            </label><br />


            <label>
                <input type="radio" name="group1"
                    checked={sort === "lowToHigh" ? true : false}
                    onChange={() =>
                        productDispatch({
                            type: "SORT_BY_PRICE",
                            payload: "lowToHigh",
                        })
                    } />
                Smartphone
            </label><br />

            <Typography sx={{ color: "orange", fontWeight: "bold", mt: 2 }} >Types: </Typography>

            <label>
                <input type="radio" name="group1"
                    checked={sort === "lowToHigh" ? true : false}
                    onChange={() =>
                        productDispatch({
                            type: "SORT_BY_PRICE",
                            payload: "lowToHigh",
                        })
                    } />
                asus
            </label><br />

            <label>
                <input type="radio" name="group1"
                    checked={sort === "lowToHigh" ? true : false}
                    onChange={() =>
                        productDispatch({
                            type: "SORT_BY_PRICE",
                            payload: "lowToHigh",
                        })
                    } />
                Lenovo
            </label><br />

            <label>
                <input type="radio" name="group1"
                    checked={sort === "lowToHigh" ? true : false}
                    onChange={() =>
                        productDispatch({
                            type: "SORT_BY_PRICE",
                            payload: "lowToHigh",
                        })
                    } />
                Mac os
            </label><br />

            <Button
                variant="light"
                onClick={() =>
                    productDispatch({
                        type: "CLEAR_FILTERS",
                    })
                }
            >
                Clear Filters
            </Button>
        </div>
    )
}

export default SidebarFilter
