import React, { useState, useContext } from 'react';
import { Typography, Box, Container, Grid, Button } from '@mui/material';
import useFetch from "../../hooks/useFetch.js";
import ProductsList from '../../components/productsList/ProductsList';
import Loading from '../../components/Loading/Loading';
import Filter from '../../components/filter/Filter.js';
import { FilterComputersState } from '../../context/filterProductContext.js';

import "./style.css";

function Products() {
  //const { data, loading, error } = useFetch(`/products`);

  const [open, setOpen] = useState(true);

  const handleClick = () => {
    setOpen(!open);
  };



  const { loading, error, state, filterComputers, updateFilters } = FilterComputersState()

  const handleCheckboxChange = (event) => {
    const { name, checked } = event.target;

    const [filterType, filterName] = name.split("-");
    console.log(state)

    // Only allow one checkbox to be selected from each filter type.

    if (checked) {
      Object.keys(state.filters[filterType]).forEach((name) => {
        updateFilters(filterType, name, false);
      });
    }

    updateFilters(filterType, filterName, checked);
  };

  console.log(state.computers)

  return (
    <React.Fragment>
      <Grid container spacing={2}>
      <Grid item xs={3} >
      <div>
      <h3>Filter by Type</h3>
      <label>
        <input
          type="checkbox"
          name="type-asus"
          checked={state.filters.type.asus}
          onChange={handleCheckboxChange}
        />
        Asus
      </label>
      <label>
        <input
          type="checkbox"
          name="type-lenovo"
          checked={state.filters.type.lenovo}
          onChange={handleCheckboxChange}
        />
        Lenovo
      </label>
      <label>
        <input
          type="checkbox"
          name="type-macos"
          checked={state.filters.type.macos}
          onChange={handleCheckboxChange}
        />
        MacOS
      </label>

      <h3>Filter by Category</h3>
      <label>
        <input
          type="checkbox"
          name="category-laptop"
          checked={state.filters.category.laptop}
          onChange={handleCheckboxChange}
        />
        Laptop
      </label>
      <label>
        <input
          type="checkbox"
          name="category-desktop"
          checked={state.filters.category.desktop}
          onChange={handleCheckboxChange}
        />
        Desktop
      </label>

      <h3>Filter by Price : </h3>
      <input
        type="range"
        min="0"
        max="5000"
        step="50"
        value={state.filters.price}
        onChange={(event) =>
          updateFilters("price", "", parseInt(event.target.value))
        }
      />
      <span>{state.filters.price} USD</span>
    </div>

    </Grid>
        <Grid item xs={9} sx={{ bgcolor: "rgba(0,0,0,0.2)", color: "black", pt: 10 }}>
          <>
            {loading ?

              <Loading />
              :
              <Box >

                <Container >
                  <ProductsList data={state.computers} />

                </Container>

              </Box>
            }

          </>
        </Grid>
      </Grid>

    </React.Fragment>
  )
}

export default Products
