import { ACTIONS } from "./FilterSidebarContext";



export const FilterListReducer = (state, action) => {
  switch (action.type) {
    case "SET_COMPUTERS":
      return { ...state, computers: action.payload }
    case ACTIONS.ALL:
      return { ...state, computers: action.payload }
    case ACTIONS.PRICE_ASC:
      const orderAscending = state.computers.sort((p, c) => p.price - c.price);
      return { ...state, computers: orderAscending }
    case ACTIONS.PRICE_DESC:
      const orderDescending = state.computers.sort((p, c) => c.price - p.price);
      return { ...state, computers: orderDescending }
    case ACTIONS.IN_STOCK:
      const inStockOnly = state.computers.filter(comp => comp.inStock === true)
      return { ...state, computers: inStockOnly }
    case ACTIONS.DELETE_FILTER:
      const ComputersFromLocalStorage = localStorage.getItem('initialComputers');
      const Computerscov = JSON.parse(ComputersFromLocalStorage);
      return { ...state, computers: Computerscov }
    default:
      return state
  }
}

export const FilterReducer = (state, action) => {

  switch (action.type) {

    case ACTIONS.INIT:
      return { ...state, products: action.payload }

    case ACTIONS.ALL:
      return { ...state, products: action.payload }

    case ACTIONS.PRICE_ASC:
      const orderAscending = state.products.sort((p, c) => p.price - c.price);
      return { ...state, products: orderAscending }

    case ACTIONS.PRICE_DESC:
      const orderDescending = state.products.sort((p, c) => c.price - p.price);
      return { ...state, products: orderDescending }

    case ACTIONS.IN_STOCK:
      const inStockOnly = state.products.filter(comp => comp.inStock === true)
      return { ...state, products: inStockOnly }

    case ACTIONS.DELETE_FILTER:
      const ComputersFromLocalStorage = localStorage.getItem('initialComputers');
      const Computerscov = JSON.parse(ComputersFromLocalStorage);
      return { ...state, products: Computerscov }

    case ACTIONS.TOGGLE_TYPE :
      const categories = state.categories.map(categorie =>
        categorie.label === categorie.payload
          ? { ...categorie, checked: !categorie.checked }
          : categorie
      )
      const filteredProducts = state.products.filter(item =>
        categories.some(categorie => categorie.checked && item.someProperty === categorie.label)
      )
      return { ...state, categorie, filteredProducts }


    default:
      return state;
  }
}
