import React, { createContext, useReducer, useContext, useEffect } from 'react'
import { computerReducer } from './Reducers';
import useFetch from '../hooks/useFetch';
const ComputerContext = createContext();

const initialState = {
    computers: [],
    categories: {
        laptop: false,
        desktop: false,
    },
    types: {
        asus: false,
        lenovo: false,
        mac: false,
        dell: false,
    },
};



export const ComputerContextProvider = ({ children }) => {

    const { data, loading, error } = useFetch(`/products`);

    const [state, dispatch] = useReducer(computerReducer, initialState);

    console.log(state.computers);

    function handleCategoryToggle(category) {
      dispatch({ type: 'TOGGLE_CATEGORY', payload: category });
    }

    function handleTypeToggle(type) {
      dispatch({ type: 'TOGGLE_TYPE', payload: type });
    }

    return (
        <ComputerContext.Provider value={{ state, loading, error, handleCategoryToggle, handleTypeToggle }}>
            {children}
        </ComputerContext.Provider>
    )
}

export const FilterComputersState = () => {
    return useContext(ComputerContext);
};
