import React, { useState } from 'react'
import { ThemeProvider, createTheme, styled } from '@mui/material/styles';
import {Switch, Grid, Paper, Box } from '@mui/material';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#F7F7F7',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#f50057',
    },
  },
});

function Profile() {
  const [mode, setMode] = useState('light');

  const toggleMode = () => {
    setMode(mode === 'light' ? 'dark' : 'light');
  };
  return (
    <ThemeProvider theme={{ ...theme, palette: { ...theme.palette, mode } }}>
        <Box sx={{width:"20%", m:"auto" }}>
        <Switch
          edge="end"
          onChange={toggleMode}

          inputProps={{
            'aria-labelledby': 'switch-list-label-wifi',
          }}
          aria-label='ssss'
        />
        <span style={{fontWeight:"bold"}}>{mode === 'light' ? 'dark' : 'light'}</span>
      </Box>

      <Grid container spacing={2} sx={{color: mode === 'light' ? 'red' : 'white'}}>
        <Grid item xs={4}>

          <Item>

          </Item>
        </Grid>
        <Grid item xs={8}>

          <Item>xs=8</Item>
        </Grid>
      </Grid>


    </ThemeProvider>
  )
}

export default Profile
