import { ACTIONS } from "./FilterSidebarContext";

ACTIONS

export const FilterListReducer = (state, action) => {
  switch (action.type) {
    case "SET_COMPUTERS":
      return { ...state, computers: action.payload }
    case ACTIONS.ALL:
      return { ...state, computers: action.payload }
    case ACTIONS.PRICE_ASC:
      const orderAscending = state.computers.sort((p, c) => p.price - c.price);
      return { ...state, computers: orderAscending }
    case ACTIONS.PRICE_DESC:
      const orderDescending = state.computers.sort((p, c) => c.price - p.price);
      return { ...state, computers: orderDescending }
    case ACTIONS.IN_STOCK:
      const inStockOnly = state.computers.filter(comp => comp.inStock === true)
      return { ...state, computers: inStockOnly }
    case ACTIONS.DELETE_FILTER:
      const ComputersFromLocalStorage = localStorage.getItem('initialComputers');
      const Computerscov = JSON.parse(ComputersFromLocalStorage);
      return { ...state, computers: Computerscov }
    default:
      return state
  }
}

export const UpdateCheckboxsReducer = (state, action) => {
  switch (action.type) {
    case ACTIONS.TOGGLE_CATEGORIE :
      const updatedCateg = { ...state.categories, [action.payload]: !state.categories[action.payload] };
      Object.keys(updatedCateg).forEach(key => {
        if (key !== action.payload && updatedCateg[key] === true) {
          updatedCateg[key] = false;
        }
      });
      return {
        ...state,
        categories: updatedCateg
      };
    case '':
      const updatedTypes = { ...state.types, [action.payload]: !state.types[action.payload] }
      Object.keys(updatedTypes).forEach(key => {
        if (key !== action.payload && updatedTypes[key] === true) {
          updatedTypes[key] = false;
        }
      });
      return {
        ...state,
        types: updatedTypes
      };
    default:
      return state;
  }
}
