import React, { useState, useEffect } from 'react';
import { Typography, Box, Paper } from '@mui/material';
import useFetch from "../../hooks/useFetch.js";
import ProductsList from '../../components/productsList/ProductsList';
import Loading from '../../components/Loading/Loading';
import "./style.css";

function Home() {
  const { data, loading, error } = useFetch(`/products`);

  return (
    <React.Fragment>
      <Paper sx={{ bgcolor: "rgba(0,0,0,0.1)", color: "black", textAlign: "center" }}>

        {loading ?
          <Loading />
          :
          <React.Fragment>
            <Typography variant="h2" sx={{ pt: 5 }} >Space-Info : </Typography>
            <Typography variant="h6" sx={{ width: "50%", m: "auto", mt: 5 }} >Space-Info Offre You Products Like Laptops, Desktops, Phones, ...
              We Have The Best Quality & The Lowest Prices, also you can make orders from home without Taxes.
            </Typography>
            <Box sx={{ mt: 10 }}>
              <Typography variant="h4">List of Some Products : </Typography>
              <ProductsList data={data} />
            </Box>
          </React.Fragment>
        }
        {error &&
          <Typography variant='h6' sx={{ color: "red", textAlign: "center", pb: 20 }}>Connection Problem, Verify Your Connect To The Internet</Typography>
        }

      </Paper>
    </React.Fragment>

  )
}

export default Home
