import React, { useReducer, createContext, useContext } from 'react'
import { FilterNavReducer, UpdateCheckboxsReducer, } from './Reducers';
import { useFetchDispatch } from '../hooks/useFetch';

const FilterSideContext = createContext();


const initialState = {
  products : [],
  productsSidenav : [],
  productsSelectopt : [],

  ALL : "ALL",
  PRICE_ASC : "price ascending",
  PRICE_DESC : "price descending",
  IN_STOCK : "in stock",
  DELETE_FILTER : "deleting filter",

  categories: {
    laptop: false,
    desktop: false,
    phone: false,
  },

  types: {
    asus: false,
    lenovo: false,
    mac: false,
    dell: false,
  },
};


export default function FilterSidebarContext({ children }) {
  const [stateFilter, dispatchFilter] = useReducer(UpdateCheckboxsReducer, initialState);

  stateFilter.products = useFetchDispatch("/products");

  function handleCategoryToggle(category) {
    dispatchFilter({ type: 'TOGGLE_CATEGORY', payload: category });
  }

  function handleTypeToggle(type) {
    dispatchFilter({ type: 'TOGGLE_TYPE', payload: type });
  }

  return (
    <FilterSideContext.Provider value={{ stateFilter, handleCategoryToggle, handleTypeToggle }}>
      {children}
    </FilterSideContext.Provider>
  )
}

export const FilterSidebarState = () => {
  return useContext(FilterSideContext);
}
