import React from 'react'
import { Box, Button, Container, Divider, Typography } from '@mui/material';
import { styled } from '@mui/material/styles';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import useFetch from '../../hooks/useFetch';
import Loading from '../Loading/Loading';
import { useLocation } from 'react-router-dom';
import laptop from '../../img/products/laptop2.jpeg';
import desktop from '../../img/products/desktop3.jpeg'
import phone from '../../img/products/phone2.jpeg';
import otherProd from '../../img/products/otherProd.jpeg';
import './style.css';
const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
}));

function ProductDetails() {
    const location = useLocation();
    const prodID = location.pathname.split("/")[2];
    const { data, loading, error } = useFetch(`/products/${prodID}`);
    console.log(data.categorie.toLowerCase())
    let imageProduct;

    if(data.categorie === "laptop"){
        imageProduct = laptop;
    }else if(data.categorie=== "desktop"){
        imageProduct = desktop; 
    }else if(data.categorie === "phone"){
        imageProduct = phone; 
    }else{
        imageProduct = otherProd;  
    }

    return (
        <>
            {loading ?
                <Loading />
                :
                <Container sx={{ pt: 10 }}>

                    <Grid container spacing={3} sx={{ bgcolor: "#e7e7e7", py: 10 }} >
                        <Grid item xs  >
                            <Box
                                component="img"
                                sx={{
                                    height: 333,
                                    width: 350,
                                    maxHeight: { xs: 233, md: 567 },
                                    maxWidth: { xs: 350, md: 350 },
                                }}
                                alt="The house from the offer."
                                src={otherProd}
                                id="imageProd"
                            />
                        </Grid>

                        <Grid item xs={6} sx={{ bgcolor: "#eeeeee" }}>

                            <Typography variant="h6" sx={{ color: "blue" }}>
                                {data.name}
                            </Typography>

                            <Typography variant="h6" sx={{ color: data.inStock ? "green" : "red", my: 2 }}>
                                {data.inStock ? " In Stock" : "Not Available"}
                            </Typography>


                            <Typography variant="body2" sx={{ my: 2 }}>
                                Total Stock : <Box component="span" sx={{ color: 'red', fontWeight: "bold" }}>{data.stock}</Box> Product
                            </Typography>

                            <Typography variant="caption" sx={{ color: "gray" }}>
                                Reference#: {data.reference}
                            </Typography>

                            <Typography variant="body2" sx={{ lineHeight: 1.8, my: 2 }}>
                                {data.description}
                            </Typography>

                        </Grid>
                        <Divider orientation="vertical" variant="middle" sx={{color:"black"}} flexItem />

                        <Grid item xs sx={{ bgcolor: "#eeeeee" }}>
                            <Typography variant="h5" sx={{ color: 'red', my: 2 }}>
                                {data.price}, 000 DT
                            </Typography>
                            <Typography variant="h6" sx={{ my: 2 }}>
                                Quantity : <input type="number" default='10' style={{ width: 50, height: 20 }} min="1" max={data.stock}></input>
                            </Typography>

                            <Button variant='contained' color='success'>
                                Add to Cart
                            </Button>
                        </Grid>

                    </Grid>
                </Container >
            }
        </>
    )
}

export default ProductDetails
