    ComputerContext
  );

  return (
    <React.Fragment>
      <Grid container spacing={2}>

        <Filter />


        <Grid item xs={9} sx={{ bgcolor: "rgba(0,0,0,0.2)", color: "black", pt: 10 }}>
          <>
            {loading ?

              <Loading />
              :
              <Box >

                <Container >
                  <ProductsList data={data} />

                </Container>

              </Box>
            }

          </>
        </Grid>
      </Grid>

    </React.Fragment>
  )
}

export default Products
