import React from 'react';
import HeroSlider, { Slide } from 'hero-slider';
import Background1 from '../../img/wallpaper_lap.jpeg';
import Background2 from '../../img/download.jpeg';
import Background3 from '../../img/laptop-transparent-png-pictures-icons-and-png-40.png';
import Background4 from '../../img/murjp1nk4lp1idlt.jpg';
import { Grid, Paper } from '@mui/material';
import styled from 'styled-components';
import { ManagerProvider } from 'react-awesome-slider/core';
import AwesomeSlider from 'react-awesome-slider';
import 'react-awesome-slider/dist/styles.css';
import withAutoplay from 'react-awesome-slider/dist/autoplay';
import 'react-awesome-slider/dist/navigation';
const AutoplaySlider = withAutoplay(AwesomeSlider);

const Item = styled.div`
  width: 100%;
  height: 100%;
`;
const images = [Background1, Background2, Background3, Background4];

const Browse = () => {
  return (
    <ManagerProvider>
      <AutoplaySlider
        play={true}
        cancelOnInteraction={false} // should stop playing on user interaction
        interval={6000}
      >
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <HeroSlider
              slidingAnimation="left-to-right"
              orientation="horizontal"
              initialSlide={1}
              onBeforeChange={(previousSlide, nextSlide) =>
                console.log('onbeforechange', previousSlide, nextSlide)
              }
              onChange={(nextSlide) => console.log('onChange', nextSlide)}
              onAfterChange={(nextSlide) => console.log('onafterchange', nextSlide)}
              style={{ backgroundColor: 'rgba(0,0,0,0.33)', maxHeight: '300px' }}
              settings={{
                slidingDuration: 250,
                slidingDelay: 100,
                shouldAutoplay: true,
                shouldDisplayButtons: true,
                autoplayDuration: 5000,
              }}
            >
              {images.map((image, index) => (
                <Slide
                  key={index}
                  background={{
                    backgroundImage: `url(${image})`,
                    backgroundAttachment: 'fixed',
                    backgroundSize: 'coverd',
                  }}
                />
              ))}
            </HeroSlider>
          </Grid>
          <Grid item xs={3}>
            <Item>
              <Slide
                background={{
                  backgroundImage: `url(${images[0]})`,
                  backgroundAttachment: 'fixed',
                  backgroundSize: 'coverd',
                }}
              />
            </Item>
          </Grid>
          <Grid item xs={3}>
            <Item>
              <Slide
                background={{
                  backgroundImage: `url(${images[1]})`,
                  backgroundAttachment: 'fixed',
                  backgroundSize: 'coverd',
                }}
              />
            </Item>
          </Grid>
          <Grid item xs={3}>
            <Item>
              <Slide
                background={{
                  backgroundImage: `url(${images[2]})`,
                  backgroundAttachment: 'fixed',
                  backgroundSize: 'coverd',
                }}
              />
            </Item>
          </Grid>
          <Grid item xs={3}>
            <Item>
              <Slide
                background={{
                  backgroundImage: `url(${images[3]})`,
                  backgroundAttachment: 'fixed',
                  backgroundSize: 'coverd',
                }}
              />
            </Item>
          </Grid>
        </Grid>
      </AutoplaySlider>
    </ManagerProvider>
  );
};

export default Browse;
