import React, { useRef, useState } from 'react'
import { Box, TextField, Container, Typography, Button, FormControl } from '@mui/material'
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles((theme) => ({
    root: {
      '& .MuiTextField-root': {
        margin: theme.spacing(1),
        width: '25ch',
      },
    },
    button: {
      margin: theme.spacing(1),
    },
  }));

function Contact() {
    const [contact, setContact] = useState("");

    const opinionRef = useRef(null);
    const errorDescription = useRef(null);

    const handleSubmit = (event) => {
        event.preventDefault()

        const message = opinionRef.current.value;
        console.log(opinionRef.current.value)
        if (message.length < 8) {
            return errorDescription.current.innerHTML = "Your Contact Message Must Contain over 7 Caracters.";
        } else {
            errorDescription.current.innerHTML = "";
        }

        const date = new Date();
        const newContact = {
            id: contact.length + 1,
            user_id: localStorage.getItem('getID'),
            content: message,
            published_date: `${date.getDate()} / ${date.getMonth()} / ${date.getFullYear()}`
        }

        setContact([...contact, newContact])

    }

    return (
        <Box sx={{ bgcolor: "silver", }}>
            <Container
                component="form"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: { xs: '25ch', md: '50ch' } },

                    pb: 20,
                    pt: 10,
                    width: "60%", m: 'auto'
                }}
                noValidate
                autoComplete="off"
            >


                <form className={classes.root} onSubmit={handleSubmit}>
                    <Typography variant='h6' sx={{ m: 1 }}>Contact us :</Typography>
                    <TextField
                        label="Contact us"
                        multiline
                        rows={5}
                        onChange={(e) => setContact(e.target.value)}
                        inputRef={opinionRef}
                    />
                    <Typography varaint='caption' ref={errorDescription} sx={{ color: "red", m: 1 }} ></Typography>
                    <Button type="submit" variant='contained' sx={{ m: 1, display: "block" }} >Add Contact</Button>
                </form>

            </Container>
        </Box>
    )
}

export default Contact
