import React, { createContext, useReducer } from 'react'
import { filterProdReducer } from './Reducers';
import useFetch from '../hooks/useFetch';
export const FilterProductContext = createContext();

export const FilterProductsContextProvider = ({children})=>{
    const { data, loading, error } = useFetch(`/products`);
    const [filterProdState, filterProdDispatch] = useReducer(filterProdReducer,{
        data
    });

    return(
        <FilterProductContext.Provider value={{filterProdState, filterProdReducer}}>
            {children}
        </FilterProductContext.Provider>
    )
}
