import { ACTIONS } from "./FilterListContext"

export const FilterListReducer = (state, action) => {
    switch (action.type) {
        case ACTIONS.ALL:
            return { ...state, computers: action.payload }
        case ACTIONS.PRICE_ASC:
            return { ...state, computers: computers.sort((p, c) => p.price - c.price ) }
        case ACTIONS.PRICE_DESC:
            return { ...state, computers: computers.sort((p, c) => c.price - p.price ) }
        case ACTIONS.IN_STOCK:
            return { ...state, computers: computers.filter(comp => comp.inStock === true ) }
        default:
            return
    }
}
