import React, { useState } from 'react'
import { Typography, Box, Grid } from '@mui/material';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { ACTIONS } from '../../context/FilterListContext';
import { FilterListState } from '../../context/FilterListContext';

function Filter() {
    const [sort, setSort] = useState('');
    const {computers, dispatchFilter} = FilterListState();

    const handleChange = (event) => {
        setSort(event.target.value);
    };

    return (
        <Box sx={{ bgcolor: "rgba(0,0,0,0.9)", borderRadius: 2, width: "70%", m: "auto", p: 1, pl: 4 }}>
            <Typography variant="h6" sx={{ color: "white" }}> Sort Computers : </Typography>

            <FormControl variant="filled" sx={{ bgcolor: 'gray', borderRadius: 2 }}>

                <Select
                    labelId="demo-simple-select-filled-label"
                    id="demo-simple-select-filled"
                    value={sort}
                    sx={{ color: "yellow", fontWeight: "bold", fontSize: 17, width: 200 }}
                    onChange={handleChange}
                >
                    <MenuItem value="All" onClick={()=> dispatchFilter({type : ACTIONS.ALL, payload: computers })} >All</MenuItem>
                    <MenuItem value={10} onClick={()=> dispatchFilter({type : ACTIONS.PRICE_DESC})} >Price Descending</MenuItem>
                    <MenuItem value={20} onClick={()=> dispatchFilter({type : ACTIONS.PRICE_DESC})} >Price Acsending</MenuItem>
                    <MenuItem value={30} onClick={()=> dispatchFilter({type : ACTIONS.IN_STOCK})} >In Stock</MenuItem>
                </Select>
            </FormControl>
        </Box>
    )
}

export default Filter
