import React from 'react'
import { Typography, Grid, CardMedia, CardContent, CardActions, Card, Button } from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import imagePa from '../../img/products/laptops/laptop1.jpeg';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import './style.css';
import { useNavigate } from 'react-router-dom';

function SingleProduct({ item }) {
    const navigate = useNavigate();
    const [age, setAge] = React.useState('');

    const handleChange = (event) => {
      setAge(event.target.value);
    };
    return (
        <Grid item xs={12} sm={6} md={4}>
            <FormControl variant="standard" sx={{ m: 1, minWidth: 120 }}>
                <InputLabel id="demo-simple-select-standard-label">Age</InputLabel>
                <Select
                    labelId="demo-simple-select-standard-label"
                    id="demo-simple-select-standard"
                    value={age}
                    onChange={handleChange}
                    label="Age"
                >
                    <MenuItem value="">
                        <em>None</em>
                    </MenuItem>
                    <MenuItem value={10}>Ten</MenuItem>
                    <MenuItem value={20}>Twenty</MenuItem>
                    <MenuItem value={30}>Thirty</MenuItem>
                </Select>
            </FormControl>
            <FormControl variant="filled" sx={{ m: 1, minWidth: 120 }}>
                <InputLabel id="demo-simple-select-filled-label">Age</InputLabel>
                <Select
                    labelId="demo-simple-select-filled-label"
                    id="demo-simple-select-filled"
                    value={age}
                    onChange={handleChange}
                >
                    <MenuItem value="">
                        <em>None</em>
                    </MenuItem>
                    <MenuItem value={10}>Ten</MenuItem>
                    <MenuItem value={20}>Twenty</MenuItem>
                    <MenuItem value={30}>Thirty</MenuItem>
                </Select>
            </FormControl>
            <Card
                sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                id="CardItem"
            >
                <CardMedia
                    component="img"
                    sx={{ height: 200 }}
                    image={imagePa}
                    alt="random"
                />
                <CardContent sx={{ flexGrow: 1 }}>
                    <Typography gutterBottom variant="h5" sx={{ mb: 1 }}>
                        {item.name.substring(0, 30)}...
                    </Typography>

                    <Typography gutterBottom variant="caption" sx={{ mb: 1 }}>
                        [ {item.reference} ]
                    </Typography>
                    <Typography gutterBottom component="h2" color="red" sx={{ mb: 1 }}>
                        {item.type}
                    </Typography>
                    <Typography component="h2" sx={{ color: item.inStock ? "green" : "red" }} >
                        {item.inStock ? "In Stock" : "Unavailable"}
                    </Typography>
                    <Typography gutterBottom color="red" component="h2" sx={{ mb: 1 }}>
                        {item.price}, 000 DT
                    </Typography>
                </CardContent>
                <CardActions sx={{ bgcolor: "black" }}>
                    <Button size="small" variant='outlined' id="btnprod"><ShoppingCartIcon />Add Product</Button>
                    <Button size="small" variant='outlined' id="btnprod" onClick={() => navigate(`/product_details/${item._id}`)}>Show Product</Button>
                </CardActions>
            </Card>
        </Grid>
    )
}

export default SingleProduct
