import React, { createContext, useContext, useReducer, useEffect, useState } from 'react'
import { FilterListReducer, UpdateCheckboxsReducer } from './Reducers';
import {useFetchDispatch} from '../hooks/useFetch';
const FiltreContext = createContext();

export const ACTIONS = {
    ALL : "ALL",
    PRICE_ASC : "price ascending",
    PRICE_DESC : "price descending",
    IN_STOCK : "in stock",
    DELETE_FILTER : "deleting filter"
}

const initState = {
    computers : [],

}

const initialState = {
    categories: {
      laptop: false,
      desktop: false,
    },
    types: {
      asus: false,
      lenovo: false,
      mac: false,
      dell: false,
    },
  };

export default function FilterListContextProvider({ children }) {

    const [state, dispatchFilter] = useReducer(FilterListReducer, initState);

    const [checkboxstate, dispatchCheckbox] = useReducer(UpdateCheckboxsReducer ,initialState);

    const [computers, setComputers] = useState([]);

    const {data, loading, error} = useFetchDispatch("/products", dispatchFilter,  setComputers);

    return (
        <FiltreContext.Provider value={{state, computers, loading, error, dispatchFilter}}>
            {children}
        </FiltreContext.Provider>
    )
}

export const FilterListState = () => {
    return useContext(FiltreContext);
}
